declare module "styled-components";
declare module "react-date-range";
declare module "js-cookie";
declare module "react-router-dom";
declare module "twin.macro";

// image type module
declare module "*.png" {
  const png: any;
  export default png;
}
declare module "*.svg" {
  const svg: any;
  export default svg;
}

declare module "*.jpg" {
  const jpg: any;
  export default jpg;
}
declare module "*.jpeg" {
  const jpeg: any;
  export default jpeg;
}

declare module "*.mp4" {
  const mp4: any;
  export default mp4;
}
